INSERT INTO `productos` (`id_producto`, `id_categoria_producto`, `area_destino`, `codigo_interno`, `nombre`, `descripcion`, `precio_unitario`, `stock`, `unidad_medida`, `imagen_url`, `estado`, `fecha_creacion`, `fecha_actualizacion`) VALUES
	(1, 1, 'cocina', NULL, 'Alitas Broaster', '14 unidades de alitas de pollo marinadas, empanizadas y fritas a presión para una piel extra crujiente y carne jugosa. Acompañadas de papas fritas y cremas variadas.', 20.00, 13, 'Unidad', 'https://www.record.com.pe/wp-content/uploads/2021/12/alitas-broaster-960x635-1-1.png', 1, '2025-05-25 07:42:44', '2025-07-09 05:14:29'),
	(2, 1, 'cocina', NULL, 'Alitas BBQ', '6 unidades de alitas de pollo marinadas, horneadas y bañadas en nuestra salsa BBQ casera. Incluye papas fritas y cremas variadas.', 30.00, 27, 'Unidad', 'https://cdn7.kiwilimon.com/recetaimagen/33623/960x640/39037.jpg.jpg', 1, '2025-05-25 07:42:44', '2025-06-30 15:39:39'),
	(3, 1, 'cocina', NULL, 'Chicharrón de Pollo', '4 piezas de pollo crujiente y jugoso, marinado en especias y frito a la perfección. Incluye papas fritas y cremas variadas.', 25.00, 42, 'Unidad', 'https://storage.googleapis.com/fitia_public_images/recipes%2FPE-R-V-00000054_py9v6w9ivzx8yjhaqa2y8z53_large.jpeg', 1, '2025-05-25 07:42:44', '2025-06-28 20:18:49'),
	(4, 1, 'cocina', NULL, 'Salchitodo', 'Trozos de pollo, hotdog, huevo frito y papas fritas. ¡Una combinación clásica y deliciosa!', 25.00, 41, 'Unidad', 'https://retrolos90.com/wp-content/uploads/2024/03/salchipapa-pobre.jpg', 1, '2025-05-25 07:42:44', '2025-06-30 15:43:33'),
	(5, 1, 'cocina', NULL, 'Salchipapa', 'Salchipapas clásicas con rebanadas de hot dog y papas fritas.', 15.00, 46, 'Unidad', 'https://elmen.pe/wp-content/uploads/2023/02/SALCHIPAPA-scaled.jpg', 1, '2025-05-25 07:42:44', '2025-06-30 19:47:40'),
	(6, 1, 'cocina', NULL, 'Porción de papas', 'Abundante porción de nuestras clásicas papas fritas, doradas y crujientes.', 10.00, 45, 'Unidad', 'https://polleriaslagranja.com/wp-content/uploads/2022/10/La-Granja-Real-Food-Chicken-Porcion-de-Papas.png', 1, '2025-05-25 07:42:44', '2025-06-30 15:17:08'),
	(7, 1, 'cocina', NULL, 'Tequeños con Queso', '13 unidades de crujientes y dorados tequeños rellenos del más cremoso queso.', 20.00, 47, 'Unidad', 'https://www.sushiloverperu.com/wp-content/uploads/2023/11/tequenos.jpg', 1, '2025-05-25 07:42:44', '2025-06-30 03:56:47'),
	(8, 2, 'bar', NULL, 'Negroni', 'Un clásico atemporal. Una mezcla equilibrada de ginebra, Campari y vermut rojo, servido sobre hielo con un twist de naranja.', 14.00, 48, 'Unidad', 'https://www.liquor.com/thmb/lbwu9mR7WPYq36cWAEbiCvGM2tQ=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/neighborhood-negroni-1200x628-fb-d5b216eacafc4c56abb866bae231bd83.jpg', 1, '2025-05-25 07:43:04', '2025-06-30 23:32:41'),
	(9, 2, 'bar', NULL, 'Clavo Oxidado', 'Un potente cóctel que combina la intensidad del whisky escocés con el dulzor del licor Drambuie, creando un sabor cálido y complejo. Servido tradicionalmente con hielo.', 18.00, 50, 'Unidad', 'https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEhyn3Vhw81hZ4qUuvewqflz_qaRUR3UED4buAdscdI9eK0D_V-TzCIz4ISsfUfy3UtQ2ZIWkuTDFkRZZZLj2sNGPxLTw15M9VgjBhpS0u2BlcwlV9Kjyrpb7IAy63B02Hqony2XZ6UrOnR_/s1600/clavo+oxidado.jpg', 1, '2025-05-25 07:43:04', '2025-06-30 23:32:59'),
	(10, 2, 'bar', NULL, 'Padrino', 'Un cóctel elegante y sofisticado que une la robustez del whisky escocés con la dulzura almendrada del licor Amaretto. Ideal para los amantes de los sabores intensos y suaves a la vez.', 18.00, 50, 'Unidad', 'https://static.wixstatic.com/media/3ede69_451e90410178409093ba24de7e00dd19~mv2.jpg/v1/fill/w_816,h_816,al_c/3ede69_451e90410178409093ba24de7e00dd19~mv2.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(11, 2, 'bar', NULL, 'Orgasmo', 'Un cóctel dulce y cremoso que combina Baileys Irish Cream, licor de café (Kahlúa) y vodka, con un toque de nata o crema. Es un postre líquido perfecto para los amantes de los sabores suaves y indulgentes.', 18.00, 50, 'Unidad', 'https://www.coctelybebida.com/wp-content/uploads/2010/07/screaming-orgasm.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(12, 2, 'bar', NULL, 'Orgasmo Múltiple', 'Una versión aún más indulgente del clásico. Combina Baileys Irish Cream, licor de café (Kahlúa), Amaretto y vodka, creando una experiencia dulce y compleja con capas de sabor. Servido con hielo y, a menudo, coronado con nata montada.', 20.00, 50, 'Unidad', 'https://images.squarespace-cdn.com/content/v1/5bcccf8b9b7d15579477a125/2a6059dd-6421-4dfd-a3be-261ecc71ac0c/Orgasm.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(13, 2, 'bar', NULL, 'Mojito Jagger', 'Un giro electrizante al clásico Mojito. Esta versión especial combina la frescura de la hierbabuena y la lima con el carácter del ron, un toque de azúcar y soda, y un ingrediente secreto que le da un toque único y vibrante.', 19.00, 50, 'Unidad', 'https://i.pinimg.com/474x/82/84/48/82844833b6f6f75313bf668ec7ac5544.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(14, 2, 'bar', NULL, 'Mojito Corona', 'Un Mojito refrescante con un toque especial. Disfruta de la frescura de la hierbabuena y la lima combinadas con ron, azúcar y un toque de cerveza Corona, que le aporta una chispa y un sabor único.', 30.00, 50, 'Unidad', 'https://cuberspremium.com/wp-content/uploads/2022/04/mojito-con-corona.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(15, 2, 'bar', NULL, 'Mojito de Limon', 'Mojito con ron, limón, azúcar, hierbabuena y soda. Refrescante y cítrico.', 18.00, 50, 'Unidad', 'https://imagenes.20minutos.es/files/image_640_auto/uploads/imagenes/2023/09/23/javier-de-las-muelas-propone-un-mojito-que-combina-ron-blanco-y-ron-dorado.png', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(16, 2, 'bar', NULL, 'Mojito de Maracuyá', 'Mojito con ron, maracuyá, azúcar, hierbabuena y soda. Tropical y refrescante.', 18.00, 50, 'Unidad', 'https://img.freepik.com/fotos-premium/mojito-maracuya-fresco-decorado-menta-sobre-mesa-madera_665346-47036.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(17, 2, 'bar', NULL, 'Mojito de Fresa', 'Mojito con ron, fresas frescas, azúcar, hierbabuena y soda. Dulce y afrutado.', 18.00, 0, 'Unidad', 'https://img.freepik.com/foto-gratis/mojito-fresa-mucho-hielo-picado_140725-3011.jpg?semt=ais_hybrid&w=740', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(18, 2, 'bar', NULL, 'Mojito de Mango', 'Mojito con ron, mango, azúcar, hierbabuena y soda. Exótico y dulce.', 18.00, 50, 'Unidad', 'https://www.mygingergarlickitchen.com/wp-content/uploads/2023/06/mango-mojito-1.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(19, 2, 'bar', NULL, 'Mojito de Piña', 'Mojito con ron, piña, azúcar, hierbabuena y soda. Tropical y jugoso.', 18.00, 50, 'Unidad', 'https://static.vecteezy.com/system/resources/previews/014/938/382/non_2x/grilled-pineapple-mojito-cocktail-photo.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(20, 2, 'bar', NULL, 'Mojito de Maracumango', 'Mojito con ron, maracuyá, mango, azúcar, hierbabuena y soda. La perfecta fusión tropical.', 19.00, 50, 'Unidad', 'https://tofuu.getjusto.com/orioneat-local/resized2/bKaKs5gH4EZ9oPR6b-2400-x.webp', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(21, 2, 'bar', NULL, 'Fernet', 'Licor amargo de hierbas, ideal para digestivos.', 17.00, 50, 'Unidad', 'https://t3.ftcdn.net/jpg/04/33/36/14/360_F_433361452_bwuYn79oAdKf4bEpvvgpYTy6Ka0kuflM.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
	(22, 2, 'bar', NULL, 'Jack Daniels', 'Clásico whisky americano, suave y con notas de vainilla y caramelo. Perfecto para beber solo o en cócteles.', 19.00, 50, 'Unidad', 'https://www.minibar24h.com/pub/media/catalog/product/cache/cabaa7e3e80c151a8132a6f6f6bcc86a/j/a/jack-daniels.jpg', 1, '2025-05-25 07:43:04', '2025-06-28 14:54:07'),
